import { useState } from 'react';
import Layout from '../../components/layout';
import ProductItem from '../../components/Product-item.js'
import fetch from 'isomorphic-unfetch';
import { createStore } from "redux";
import { Provider } from "react-redux";
import { cartReducer } from "../../components/reducer";
import { connect } from "react-redux";
import store from '../../components/store'
import { DataConsumer } from '../../components/cartcontext.js'

const Productitem = props => {

  const addItem = (product) => {
     const cart = [];
     cart.concat(product);
     console.log(cart);
  }
  return (
    <ProductItem product={props.product} addItem={addItem} />
  )

};

Productitem.getInitialProps = async function(context) {
  const { id } = context.query;
  const res = await fetch(`https://my-json-server.typicode.com/mood-board/demo/products/${id}`);
  const product = await res.json();
  return { product };
};

export default Productitem;
